const Blessed = require('blessed')
const runCmd = require('../commands/index').run

let screen = null
let output = null
module.exports.init = () => {
    screen = Blessed.screen({
        fullUnicode: true
    })
    output = Blessed.log({
        top: 0,
        left: 0,
        height: '100%-3',
        width: '100%',
        keys: true,
        mouse: true,
        scrollable: true,
        scrollbar: {
            ch: ' ',
            bg: 'red'
        },
        border: {
            type: 'line'
        },
        label: ' Log '
    })
    let input = Blessed.textbox({
        bottom: 0,
        left: 0,
        height: 3,
        width: '100%',
        keys: true,
        mouse: true,
        inputOnFocus: true,
        style: {
            fg: 'white',
        },
        border: {
            type: 'line'
        },
        label: ' Input '
    })

    screen.key(['escape', 'C-c'], () => process.exit(0))
    screen.append(output)
    screen.append(input)

    input.on('submit', text => {
        runCmd(text)
        input.clearValue()
        input.focus()
    })

    input.focus()
}

module.exports.log = text => output ? output.log(text) : console.log(text)

module.exports.dump = text => {
    screen.destroy()
    console.log(text)
}

module.exports.banner = () => console.log(Buffer.from('', 'hex').toString('utf8'))
